import React, { useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { Helmet } from "react-helmet";
import IssueHead from "./IssueHead";
import Comments from "./Comments";
import axios from "axios";


const Issues = () => {
  let { id } = useParams();

  useEffect(() => {
    axios.get(`http://localhost:4000/issues/${id}`)
      .then(res => console.log(res))
      .catch(err => console.log(err))
  }, [])

  return (
    <div>
      <Helmet>
        <link rel="stylesheet" href="../css/home.css" />
      </Helmet>
      <IssueHead />
      <div className="container">
        <div className="row">
          <div>
            <div
              style={{ display: "flex", letterSpacing: "0.5px", gap: "0.4rem" }}
            >
              <h2 style={{ fontWeight: 400 }}>title</h2>
              <h2 style={{ color: "#57606a", fontWeight: "300" }}>#1</h2>
            </div>
            <div>
              <button
                className="btn btn-success"
                style={{
                  fontWeight: 600,
                  background: "#2da44e",
                  borderRadius: "20px",
                  padding: "6px 18px",
                }}
              >
                <i className="far fa-scrubber"> Open</i>
              </button>
            </div>
          </div>
        </div>
        <hr />
        <div className="row">
          <Comments />
          <div className="col-sm-2">
            <select
              className="form-select"
              id="label"
              aria-label="Default select example"
              style={{
                all: "unset",
                color: "#57606a",
                fontWeight: 600,
                fontSize: "14px",
              }}
              name="label"
              //   onChange="handleLabel"
            >
              <option value={0}>Labels</option>
            </select>
            <hr />
            <select
              className="form-select"
              id="label"
              aria-label="Default select example"
              style={{
                all: "unset",
                color: "#57606a",
                fontWeight: 600,
                fontSize: "14px",
              }}
              name="assignee"
              //   onChange="handleLabel"
            >
              <option value={0}>Assignees</option>
            </select>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Issues;
