import React from 'react';
import { Link } from 'react-router-dom';
// import Editor from "rich-markdown-editor";
import ReactQuill, { Quill } from 'react-quill'; // ES6
import 'react-quill/dist/quill.snow.css'; 

const NewIssue = () => {
	return (
		<div>
			<div style={{ backgroundColor: '#f6f8fa' }}>
				<div className="container">
					<div className="row">
						<div className="col" style={{ marginTop: '20px' }}>
							<a className="projTitle">Project_Name </a>
						</div>
					</div>
				</div>
				<div className="row tabs">
					<div className="col-sm issueTab">
						<i className="far fa-scrubber" />
						<p>
                            <Link to='/' style={{textDecoration: 'none', color: '#000'}}>Issues</Link>
                        </p>
					</div>
				</div>
				<hr style={{ marginTop: '0px' }} />
			</div>

            <div>
                <form action="/create-issue" method="POST" style={{ display: 'flex' }}>
                    <div className="col-sm-1"> </div>
                    <div className="col-sm-9 newIssue">
                        <input className="issueTitle" type="text" placeholder="  Title" name="title" />
                        {/* <input id="markdown-editor" type="textarea" value="" name="editor" placeholder="Leave a comment" /> */}
						{/* <Editor /> */}
						<ReactQuill style={{display: 'grid', marginTop: "20px"}} >
							<div className="my-editing-area" style={{borderRadius: '0 0 5px 5px'}} />
						</ReactQuill> 
                        <button className="btn btn-success btnSubmit">Submit new issue</button>
                    </div>
                    <div className='col-sm-2'>
                    <select class="form-select" aria-label="Default select example" style={{all: "unset", color: "#57606a", fontWeight: "600", fontSize: "14px"}} name="label">
                        <option value={0}>Labels</option>
                        <option value={1}>Bug</option>
                    </select>

                    </div>
                </form>
            </div>
			<style>{`
				.quill {
					// background: red !important;
					// height: 100px;
				}

				.ql-toolbar {
					border-radius: 5px 5px 0 0;
				}

				.ql-snow {
					// border-radius: 0 0 5px 5px;
				}
			`}
			</style>
		</div>
	);
};
// const CustomToolbar = () => (
// 	<div id="toolbar" style={{background: 'red'}}>
// 	</div>
// )

// NewIssue.modules = {
// 	toolbar: {
// 		container: "#toolbar",
// 	}
// }

export default NewIssue;
