// import './App.css';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.css'
import { Component } from 'react';
import Navbar from './components/Navbar';
import TodoForm from './components/TodoForm';
import TodoList from './components/TodoList';

class App extends Component {
  state = {
    items: []
  }

  componentDidMount() {
    axios.get('http://localhost:4000/home')
      .then(res => {
        this.setState({items:  res.data.issues})
      })
      .catch(err => console.log(err))
  }

  addTodo(item) {
    console.log('skjhfdksjf')
    // this.setState({
    //   items: [...this.state.items, item]
    // })
    axios.post('http://localhost:4000/create-issue', {
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(item)
    })
      .then(res => console.log(res))
      .catch(err => console.log(err))
  }


  addTodo(item) {
    this.setState({
      items: [...this.state.items, item]
    })
  }

  deleteTodo(item) {
    console.log(item)
    let newItems = this.state.items.filter(i => i !== item)
    this.setState({items: newItems})
  }

  render() {
    return (
        <div className='container'>
          <Navbar />
          <TodoForm  addTodo={this.addTodo.bind(this)}/>
          <TodoList items={this.state.items} deleteTodo={this.deleteTodo.bind(this)}/>
        </div>
    );
  }
}

export default App;
