import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import ReactQuill, { Quill } from 'react-quill'; // ES6
import 'react-quill/dist/quill.snow.css'; 

const NewIssue = () => {
	const [input, setInput] = useState('')
	const [title, setTitle] = useState('')

	const handleChange = (value) => {
		setTimeout(() => {
			setInput(value)
		});
	}

	useEffectect(() => {
		handleChange(input)
	}, [input])

	return (
		<div>
			<div style={{ backgroundColor: '#f6f8fa' }}>
				<div className="container">
					<div className="row">
						<div className="col" style={{ marginTop: '20px' }}>
							<a className="projTitle">Project_Name </a>
						</div>
					</div>
				</div>
				<div className="row tabs">
					<div className="col-sm issueTab">
						<i className="far fa-scrubber" />
						<p>
                            <Link to='/' style={{textDecoration: 'none', color: '#000'}}>Issues</Link>
                        </p>
					</div>
				</div>
				<hr style={{ marginTop: '0px' }} />
			</div>

            <div>
                <form action="/create-issue" method="POST" style={{ display: 'flex' }}>
                    <div className="col-sm-1"> </div>
                    <div className="col-sm-9 newIssue">
                        <input className="issueTitle" type="text" placeholder="  Title" name="title"  value={title} onChange={(e) => setTitle(e.target.value)}/>
                        {/* <input id="markdown-editor" type="textarea" value="" name="editor" placeholder="Leave a comment" /> */}
						{/* <Editor /> */}
						<ReactQuill 
						style={{display: 'grid', margin: "20px 0"}} 
						value={input} 
						onChange={handleChange}
						// modules={ReactQuill.modules}
						>
							<div className="my-editing-area" style={{borderRadius: '0 0 5px 5px', height: '300px', backgroundColor: '#f6f8fa', fontSize: '1rem'}} />
						</ReactQuill> 
                        <button className="btn btn-success btnSubmit">Submit new issue</button>
                    </div>
                    <div className='col-sm-2'>
                    <select className="form-select" aria-label="Default select example" style={{all: "unset", color: "#57606a", fontWeight: "600", fontSize: "14px"}} name="label">
                        <option value={0}>Labels</option>
                        <option value={1}>Bug</option>
                    </select>
					<hr />
					<select className="form-select" id="label" aria-label="Default select example" style={{all: "unset", color: "#57606a", fontWeight: "600", fontSize: "14px"}} name="assignee">
						<option value="0">Assignees</option>
					</select>
                    </div>
                </form>
            </div>
			<style>{`
				.ql-toolbar {
					border-radius: 5px 5px 0 0;
				}
			`}
			</style>
		</div>
	);
};

export default NewIssue;
