// import './App.css';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.css'
import { Component } from 'react';
import Navbar from './components/Navbar';
import TodoForm from './components/TodoForm';
import TodoList from './components/TodoList';

class App extends Component {
  state = {
    items: []
  }

  componentDidMount() {
    axios.get('http://localhost:4000/home')
      .then(res => {
        this.setState({items:  res.data.issues})
      })
      .catch(err => console.log(err))
  }

  addTodo(item) {
    axios.post('http://localhost:4000/create-todo', {item: item})
      .then(res => {
        this.setState({
          items: [...this.state.items, {comment: res.data.response[0]}]
        })
    })
      .catch(err => console.log(err))
  }


  deleteTodo(item) {
    console.log(item)
    // let newItems = this.state.items.filter(i => i !== item)
    axios.post('http://localhost:4000/delete-todo', {item})
    .then(res => {
      this.setState({
        items: res.data.issues
      })
    })
    .catch(err => console.log(err))
    // this.setState({items: newItems})
  }

  render() {
    return (
      <>
        <Navbar />
        <div className='container'>
          <TodoForm  addTodo={this.addTodo.bind(this)}/>
          <TodoList items={this.state.items} deleteTodo={this.deleteTodo.bind(this)}/>
        </div>
      </>
    );
  }
}

export default App;
