import React, { useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { Helmet } from "react-helmet";
import IssueHead from "./IssueHead";
import Comments from "./Comments";
import axios from "axios";
import { useState } from "react/cjs/react.development";
import ReactQuill, { Quill } from "react-quill"; // ES6
import "react-quill/dist/quill.snow.css";

const Issues = () => {
  let { id } = useParams();
  const [comments, setComments] = useState(null);
  const [input, setInput] = useState("");
  let user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    axios
      .get(`http://localhost:4000/issues/${id}`)
      .then((res) => {
        console.log(res);
        setComments(res.data.issue);
      })
      .catch((err) => console.log(err));
  }, []);

  const handleComment = () => {
    axios
      .post("http://localhost:4000/create-comment", {
        id,
        input,
        username: user.username,
      })
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  };

  return (
    <div>
      <Helmet>
        <link rel="stylesheet" href="../css/home.css" />
      </Helmet>
      <IssueHead />
      <div className="container">
        <div className="row">
          <div>
            <div
              style={{ display: "flex", letterSpacing: "0.5px", gap: "0.4rem" }}
            >
              <h2 style={{ fontWeight: 400 }}>title</h2>
              <h2 style={{ color: "#57606a", fontWeight: "300" }}>#1</h2>
            </div>
            <div>
              <button
                className="btn btn-success"
                style={{
                  fontWeight: 600,
                  background: "#2da44e",
                  borderRadius: "20px",
                  padding: "6px 18px",
                }}
              >
                <i className="far fa-scrubber"> Open</i>
              </button>
            </div>
          </div>
        </div>
        <hr />
        <div className="row">
          {/* <Comments comments={comments}/> */}
          <div className="col-sm-2">sai sia</div>
          <div className="col-sm-8">
            <table
              className="table"
              style={{ borderCollapse: "separate", borderSpacing: 0 }}
            >
              <thead style={{ background: "#ddf4ff" }}>
                <tr>
                  <th
                    style={{
                      border: "1px solid #a6d9ff",
                      borderRadius: "5px 5px 0 0",
                    }}
                    scope="col"
                  >
                    sai sia
                  </th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td
                    style={{
                      border: "1px solid #a6d9ff",
                      borderRadius: "0 0 5px 5px",
                    }}
                    scope="row"
                  >
                    Comment
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
          {comments &&
            comments.map((comment) => (
              <>
                <div className="col-sm-2"></div>
                <div className="col-sm-2">sai sia</div>
                <div className="col-sm-8">
                  <table
                    className="table"
                    style={{ borderCollapse: "separate", borderSpacing: 0 }}
                  >
                    <thead style={{ background: "#ddf4ff" }}>
                      <tr>
                        <th
                          style={{
                            border: "1px solid #a6d9ff",
                            borderRadius: "5px 5px 0 0",
                          }}
                          scope="col"
                        >
                          sai sia
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <td
                          style={{
                            border: "1px solid #a6d9ff",
                            borderRadius: "0 0 5px 5px",
                          }}
                          scope="row"
                        >
                          Comment
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </>
            ))}
          <div className="col-sm-2">
            <select
              className="form-select"
              id="label"
              aria-label="Default select example"
              style={{
                all: "unset",
                color: "#57606a",
                fontWeight: 600,
                fontSize: "14px",
              }}
              name="label"
              //   onChange="handleLabel"
            >
              <option value={0}>Labels</option>
            </select>
            <hr />
            <select
              className="form-select"
              id="label"
              aria-label="Default select example"
              style={{
                all: "unset",
                color: "#57606a",
                fontWeight: 600,
                fontSize: "14px",
              }}
              name="assignee"
              //   onChange="handleLabel"
            >
              <option value={0}>Assignees</option>
            </select>
          </div>
          <div className="col-sm-2"></div>
          <div className="col-sm-8">
            <ReactQuill
              style={{ display: "grid", margin: "20px 0" }}
              value={input}
              onChange={setInput}
              placeholder="Leave a Comment"
            ></ReactQuill>
            <button
              className="btn btn-success"
              style={{ backgroundColor: "#2da44e" }}
              onClick={handleComment}
            >
              Comment
            </button>
          </div>
        </div>
      </div>
      <style>
        {`
				.ql-toolbar {
					border-radius: 5px 5px 0 0;
				}

				.ql-container {
					height: 100px;
					border-radius: 0 0 5px 5px;
					background-color: #f6f8fa;
					font-size: 1rem;
				}
			`}
      </style>
    </div>
  );
};

export default Issues;
